svg.script:
(function(){
  const svgNS = "http://www.w3.org/2000/svg";
  const w = 512;
  const n = 100;               // número de bandas
  const step = w / n;         // distância entre tamanhos consecutivos

  const bandsGroup = document.getElementById('bands');
  const bordersGroup = document.getElementById('borders');

  // desenha de fora para dentro: i=0 => branco absoluto (outer), i=n-1 => preto absoluto (inner)
  for (let i = 0; i < n; i++) {
    const size = w - i * step;
    const x = (w - size)/2;
    const y = x;

    // intensidade de cinza (255->0)
    const ratio = i / (n - 1);
    const v = Math.round(255 * (1 - ratio));
    const hex = v.toString(16).padStart(2,"0");
    const fill = "#" + hex + hex + hex;

    // rect preenchida (a banda)
    const r = document.createElementNS(svgNS, 'rect');
    r.setAttribute('x', x.toFixed(3));
    r.setAttribute('y', y.toFixed(3));
    r.setAttribute('width', size.toFixed(3));
    r.setAttribute('height', size.toFixed(3));
    r.setAttribute('fill', fill);
    bandsGroup.appendChild(r);

    // rect apenas com stroke (fronteira dourada translúcida)
    const s = document.createElementNS(svgNS, 'rect');
    s.setAttribute('x', x.toFixed(3));
    s.setAttribute('y', y.toFixed(3));
    s.setAttribute('width', size.toFixed(3));
    s.setAttribute('height', size.toFixed(3));
    s.setAttribute('fill', 'none');

    // tornar a largura da linha proporcional (ex.: mais grossa nas maiores)
    // aqui mantemos 1.6 -> 1.0 px aproximado: adaptável
    const strokeW = 2 * Math.max(0.6, ( (n - i) / n ));
    s.setAttribute('stroke-width', strokeW.toFixed(2));
    s.setAttribute('stroke', 'url(#goldFade)');
    s.setAttribute('stroke-opacity', '0.55');
    bordersGroup.appendChild(s);
  }

  // Small tweak: ensure the very inner area is solid black so the Phi sits on absolute black
  // create a final inner square slightly smaller than the last band (to avoid border overlap)
  const innerSize = w - (n-1) * step - 2.0; // small inset
  if (innerSize > 0) {
    const ix = (w - innerSize)/2;
    const inner = document.createElementNS(svgNS, 'rect');
    inner.setAttribute('x', ix.toFixed(3));
    inner.setAttribute('y', ix.toFixed(3));
    inner.setAttribute('width', innerSize.toFixed(3));
    inner.setAttribute('height', innerSize.toFixed(3));
    inner.setAttribute('fill', '#000000');
    // inserir no topo das bandas, mas abaixo do símbolo
    bandsGroup.appendChild(inner);
  }

  // Ajuste final: coloca o grupo do símbolo acima de tudo (já está por cima no markup),
  // e garante que o Phi esteja centrado (transform já definido).
})();
